package examPactice;

public class DataTypesAndVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// Primitive Data Types (built-in)
		
//		Type	Description					Size			Example
//		byte	Whole number				1 byte		byte a = 100;  -128 to 127
//		short	Whole number				2 bytes		short s = 500; -32768 to 32767
//		int		Whole number (default)		4 bytes		int x = 2000;
//		long	Large whole number			8 bytes		long l = 90000L;
//		float	Decimal number				4 bytes		float f = 5.75f; 5 to 6 digit after decimal
//		double	Larger decimal (default)	8 bytes		double d = 19.99;
//		char	Single character			2 bytes		char c = 'A';
//		boolean	True/false					1 bit 		boolean b = true;
//		 Non-Primitive Data Types
//		 These refer to objects and are more complex:
//
//		1. String: String name = "John";
//
//		 2.Arrays, Classes, Interfaces, etc.
		
		int age = 90;
		double p1 = 3.14;
		boolean answer = true;
		byte a = 10;
		short b =1234;
		long c = 876533345678765L;
		char initial ='G';
		String name ="Rahul";
		float value = 8547.36f;
		
		System.out.println("Age: "+age);
		System.out.println("P1 value: "+p1);
		System.out.println("Answer: "+answer);
		System.out.println("byte : "+a);
		System.out.println("short value "+b);
		System.out.println("C value "+c);
		System.out.println("Initial  "+initial);
		System.out.println("Name  "+name);
		System.out.println("value "+value);
	}
	

}
